import { Component } from '@angular/core';
import { RouterOutlet } from '@angular/router';
import {LoanCardComponent} from './components/loan/loan-card/loan-card.component';
import {CreateLoanComponent} from './components/loan/create-loan/create-loan.component';
import {EditClientComponent} from './components/client/edit-client/edit-client.component';
import {EditClient} from './components/loan/loan-card.component.models';

@Component({
  selector: 'app-root',
  imports: [CreateLoanComponent, EditClientComponent],
  templateUrl: './app.component.html',
  styleUrl: './app.component.scss'
})
export class AppComponent {
  title = 'confiape-loans-ui';
  clientData: EditClient = {
    name: 'warren',
    alias: 'aroni',
    dni: '4772445',
    cellphone: '937373210',
    tags: ["wa"]
  };
}

import {Component, input, OnInit, output} from '@angular/core';
import {FormBuilder, FormGroup, ReactiveFormsModule, Validators} from '@angular/forms';
import {NgIf} from '@angular/common';
import {NewLoan} from '../loan-card.component.models';

@Component({
  selector: 'app-create-loan',
  imports: [
    ReactiveFormsModule,
    NgIf
  ],
  templateUrl: './create-loan.component.html'
})
export class CreateLoanComponent implements OnInit {
  loanForm!: FormGroup;
  clientName = input.required<string>();
  clientId = input.required<string>();

  onLoanCreate = output<{ loan: NewLoan, id: string }>()

  constructor(private fb: FormBuilder) {
  }

  ngOnInit() {
    this.loanForm = this.fb.group({
      amount: ['', [Validators.required, Validators.min(0)]],
      date: ['', Validators.required],
      duration: ['27', [Validators.required, Validators.min(1)]],
      interestRate: ['8.4', [Validators.required, Validators.min(0), Validators.max(100)]],
      loanType: ['daily', Validators.required],
    });
  }

  onSubmit() {
    if (this.loanForm.valid) {
      this.onLoanCreate.emit({
        id: this.clientId(),
        loan: this.loanForm.value
      })
    }
  }

  onCancel() {

  }
}

import {Component, input, OnInit, output} from '@angular/core';
import {FormBuilder, FormGroup, ReactiveFormsModule, Validators} from '@angular/forms';
import {EditClient} from '../../loan/loan-card.component.models';
import {NgForOf, NgIf} from '@angular/common';
import {MatButtonToggle, MatButtonToggleGroup} from '@angular/material/button-toggle';

@Component({
  selector: 'app-edit-client',
  imports: [
    ReactiveFormsModule,
    NgIf,
    NgForOf,
    MatButtonToggleGroup,
    MatButtonToggle
  ],
  templateUrl: './edit-client.component.html',


})
export class EditClientComponent implements OnInit {
  clientData = input.required<EditClient>();
  availableTags = input.required<string[]>();

  onEditClient = output<EditClient>();
  onCancelEdit = output();
  editClientForm!: FormGroup;


  constructor(private fb: FormBuilder) {
  }

  ngOnInit() {
    this.editClientForm = this.fb.group({
      name: [this.clientData().name || '', [Validators.required]],
      alias: [this.clientData().alias || ''],
      dni: [this.clientData().dni || '', [Validators.required]],
      cellphone: [this.clientData().cellphone || ''],
      tags: [this.clientData().tags || [], Validators.required],
    });
  }

  onSubmit() {
    if (this.editClientForm.valid) {
      this.onEditClient.emit(this.editClientForm.value);
    }
  }

  onCancel() {
    this.onCancelEdit.emit();
  }
}

import {Component, input, output} from '@angular/core';
import {CurrencyPipe, DatePipe, NgClass} from '@angular/common';
import {Loan, Client} from '../loan-card.component.models';


@Component({
  selector: 'app-loan-card',
  imports: [
    NgClass,
    CurrencyPipe,
    DatePipe
  ],
  templateUrl: './loan-card.component.html'
})
export class LoanCardComponent {
  client = input.required<Client>();

  onEditClient = output<Client>()
  onViewLoanDetails = output<Loan>()
  onCreateLoan = output()

  getStatusClass(): string {
    const statusClasses = {
      'active': 'bg-green-100 text-green-800',
      'overdue': 'bg-red-100 text-red-800',
      'paid': 'bg-gray-100 text-gray-800'
    };
    return statusClasses['paid'] || '';
  }


  createLoan(): void {
    this.onCreateLoan.emit()
  }

  editClient() {
    this.onEditClient.emit(this.client())
  }

  viewLoanDetails(loan:Loan) {
    this.onViewLoanDetails.emit(loan);
  }
}

