import {Component, EventEmitter, input, Output} from '@angular/core';
import {CurrencyPipe, DatePipe, NgClass, NgIf} from '@angular/common';

@Component({
  selector: 'app-loan-card',
  imports: [
    NgClass,
    CurrencyPipe,
    DatePipe
